web3 = getWeb3Instance(
    rpcUrl=rpcUrl
)

routerAddress = web3.toChecksumAddress("0x3C351E1afdd1b1BC44e931E12D4E05D6125eaeCa")
routerAddressLower = web3.toChecksumAddress("0x3C351E1afdd1b1BC44e931E12D4E05D6125eaeCa").lower()

loop = asyncio.get_event_loop()
allBlocks, processedBlockRange = loop.run_until_complete(getAllTransactionsForBlockRange(rpcUrl=rpcUrl, returnFullTransaction=True))
successfulBlocks = [block for block in allBlocks if block['success']]
successfulTransactions = [block["result"]["transactions"] for block in successfulBlocks]
allBlockTransactions = [j for i in successfulTransactions for j in i]
allRouterTransactions = [transaction for transaction in allBlockTransactions if transaction['to'] == routerAddressLower]
allTransactionInputs = [transaction["input"] for transaction in allRouterTransactions]
uniqueBlockTransactions = list(set(allTransactionInputs))

collectedRoutes = {}
for uniqueBlockTransaction in uniqueBlockTransactions:

    output = decodeTx(routerAddress, uniqueBlockTransaction, routerAbiStr)

    if isinstance(output, dict) and "path" in output["params"]:

        routeUsed = output["params"]["path"]
        routeName = f"{routeUsed[0]}-{routeUsed[-1]}"

        isLoopRoute = routeUsed[0] == routeUsed[-1]

        if not isLoopRoute:

            if routeName not in collectedRoutes:
                collectedRoutes[routeName] = []

            routeObject = {
                "method": output["name"],
                "route": routeUsed
            }

            if "amountIn" in output["params"]:
                routeObject["amountIn"] = output["params"]["amountIn"]

            if "amountOutMin" in output["params"]:
                routeObject["amountOutMin"] = output["params"]["amountOutMin"]

            if routeObject not in collectedRoutes[routeName]:

                collectedRoutes[routeName].append(routeObject)

                print(collectedRoutes[routeName])

